mapper.insert:
INSERT INTO MEMBER VALUES(
	MEMBER_SEQ.NEXTVAL, #{id}, #{pw}, #{name}, #{tel}, #{email}, #{address}, SYSDATE, 0
)

mapper.select:
SELECT COUNT(*)
  FROM MEMBER
 WHERE ID = #{id}

SELECT COUNT(*)
  FROM MEMBER
 WHERE EMAIL = #{email}

mapper.update:
UPDATE MEMBER
   SET NAME = #{name}
     , ADDRESS = #{address}



	, PW = #{pw}


 WHERE MEMBER_NO = #{memberNo}

UPDATE MEMBER
   SET STATE = -1
 WHERE MEMBER_NO = #{memberNo}

